import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

//const example
const currDate = () => {
    const date = new Date();
    return date.toDateString();
}

const starter = <h1>Hello Tarcisius<br/>Current Date: {currDate()}</h1>;

//making classes exercises
class Car extends React.Component{
    constructor(name){
        this.brand = name;
    }

    render(){
        return 'I have a ' + this.brand;
    }    
}

class CarType extends Car{
    constructor(name, spec){
        super(name);
        this.type = spec;
    }

    render(){
        return <Car /> + ', the specific type is ' + this.type;
    }
}
var myCar = new CarType('BMW', 'M4');
ReactDOM.render(<myCar />, document.getElementById('root'));

ReactDOM.render(starter, document.getElementById('root'));

